class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& vecH, vector<int>& vecV) {

        sort(vecH.begin(), vecH.end(), greater<int>());
        sort(vecV.begin(), vecV.end(), greater<int>());

        long long int pH = 1, pV = 1;

        long long int l1=0, l2=0;
        long long int ans = 0;

        while(l1 < vecH.size() && l2 < vecV.size())
        {
            if(vecH[l1] > vecV[l2])
            {
                ans += vecH[l1]*pH;
                l1++;
                pV++;
            }
            else
            {
                ans += vecV[l2]*pV;
                l2++;
                pH++;
            }
        }

        while(l1 < vecH.size())
        {
            ans += vecH[l1]*pH;
            l1++;
        }

        while(l2 < vecV.size())
        {
            ans += vecV[l2]*pV;
            l2++;
        }

        return ans;
    }
};
