class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>> v;
        for(int i=0;i<m-1;i++) v.push_back({horizontalCut[i],0});
        for(int i=0;i<n-1;i++) v.push_back({verticalCut[i],1});
        long long ans=0;
        long long hm=1,vm=1;
        sort(v.rbegin(),v.rend());
        for(int i=0;i<v.size();i++){
            int cost=v[i].first,dir=v[i].second;
            if(dir==0){
                ans+=cost*hm;
                vm++;
            }
            else if(dir==1){
                ans+=cost*vm;
                hm++;
            }
        }
        return ans;
    }
};
