class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& horizontal, vector<int>& vertical) {
        long long hor = 1, ver = 1;
        sort(horizontal.begin(), horizontal.end());
        long long cost = 0;
        sort(vertical.begin(), vertical.end());
        int i = n-2, j = m-2;
        while(i>=0 and j>=0){
            int minn = max(horizontal[i], vertical[j]);
            if(minn == horizontal[i]){
                cost+=(long long)minn*hor*1LL;
                ver++;
                i--;
            }
            else{
                cost+=(long long)minn*ver*1LL;
                hor++;
                j--;
            }
        }
        while(i>=0){
            cost+=(long long)horizontal[i]*(hor)*1LL;
            ver++;
            i--;
        }
        while(j>=0){
            cost+=(long long)vertical[j]*(ver)*1LL;
            hor++;
            j--;
        }
        return cost;
    }
};
