class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
       sort(hc.begin(), hc.end(), greater<int>());
        sort(vc.begin(), vc.end(), greater<int>());
        int h = 0, v = 0, hp = 1, vp = 1;
        long long ans = 0;

        while (h < hc.size() && v < vc.size()) {
            if (hc[h] > vc[v]) {
                ans += hc[h++] * vp; 
                hp++;
            }
            else{ 
                ans += vc[v++] * hp;
                 vp++;
            }
        }
        while (h < hc.size()) ans += hc[h++] * vp;
        while (v < vc.size()) ans += vc[v++] * hp;

        return ans; 
    }
};
