class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        long long int ans=0;
        long long int xdiv=1, ydiv=1;
        sort(hcut.begin(), hcut.end(), greater<int>());
        sort(vcut.begin(), vcut.end(), greater<int>());

        int i=0, j=0;

        while(i<hcut.size() && j<vcut.size()){
            if(hcut[i]>vcut[j]){
                ans=ans + hcut[i]*ydiv;
                i++;
                xdiv++;
                // cout<<xdiv<<" ";
            }
            else{
                ans=ans + vcut[j]*xdiv;
                j++;
                ydiv++;
                // cout<<ydiv<<" |";
            }
        }

        while(i<hcut.size()){
            ans=ans+hcut[i]*ydiv;
            i++;
        }
        while(j<vcut.size()){
            ans=ans+vcut[j]*xdiv;
            j++;
        }

        return ans;

    }
};
