class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        pq = []
        for i, cost in enumerate(horizontalCut):
            heappush(pq, (-cost, i, 'h'))
        for j, cost in enumerate(verticalCut):
            heappush(pq, (-cost, j, 'v'))
        width, height = 1, 1
        res = 0
        while pq:
            cost, idx, t = heappop(pq)
            if t == 'v':
                res -= cost * width
                height += 1
            else:
                res -= cost * height
                width += 1
        return res
