class Solution:
  def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:

    arr = []
    # 0 - horizontal cut
    # 1 - vertical cut
    for h in hc:
      arr.append([h, 0])
    for v in vc:
      arr.append([v, 1])
    arr.sort(key=lambda x: -x[0])
    # print(arr)
    total_pieces = m * n 
    h_cuts = v_cuts = 0
    min_cost = 0
    for cost, typ in arr:
      if typ == 0:
        min_cost += (v_cuts + 1) * cost
        h_cuts += 1
      else:
        min_cost += (h_cuts + 1) * cost
        v_cuts += 1
    return min_cost
