class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
          multiset<pair<int,pair<int,int>>>st;
        for(int i=0;i<h.size();i++){
            st.insert({h[i],{1,i}});
        }
        for(int i=0;i<v.size();i++){
            st.insert({v[i],{2,i}});
        }
        long long ans=0;
        int hcount=1;
        int vcount=1;
        while(!st.empty()){
            auto it=*st.rbegin();

            int cost=it.first;
            int type=it.second.first;

            st.erase(it);

            if(type==1){
                ans+=cost*hcount;
                vcount++;
            }else if(type==2){
                ans+=cost*vcount;
                hcount++;
            }

        }
        return ans;
    }
};
