class Solution {
public:

  long long mini( vector<int>& sortedX,  vector<int>& sortedY) {
        long long res = 0;



        sort(sortedX.begin(), sortedX.end(), greater<int>());
        sort(sortedY.begin(), sortedY.end(), greater<int>());

        int hzntl = 1, vert = 1;

        int i = 0, j = 0;
        int m = sortedX.size();
        int n = sortedY.size();

        while (i < m && j < n) {
            if (sortedX[i] > sortedY[j]) {
                res += sortedX[i] * vert;

                hzntl++;
                i++;
            } else {
                res += sortedY[j] * hzntl;

                vert++;
                j++;
            }
        }

        while (i < m) {
            res += sortedX[i] * vert;
            i++;
        }

        while (j < n) {
            res += sortedY[j] * hzntl;
            j++;
        }

        return res;
    }

    long long minimumCost(int m, int n, vector<int>& hori, vector<int>& verti) {
        return mini(hori,verti);
    }
};
