class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<vector<int>> cuts;
        for(int i:h)
        cuts.push_back({i, 0});
        for(int i:v)
        cuts.push_back({i, 1});
        sort(cuts.begin(), cuts.end(), greater<vector<int>>());
        long long hp=1, vp=1, ans=0;
        for(vector<int> cut:cuts){
            if(cut[1]==0){
                ans+=(long long)cut[0]*vp;
                hp++;
            }
            else{
                ans+=(long long)cut[0]*hp;
                vp++;
            }
        }
        return ans;
    }
};
