class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = sorted([(c, 0) for c in horizontalCut] + [(c, 1) for c in verticalCut])
        x = y = 1
        ans = 0
        for ct, xy in cuts[::-1]:
            if xy == 0:
                x += 1
                ans += ct * y
            else:
                y += 1
                ans += ct * x
        return ans
