import heapq

class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = []
        heapq.heapify(cuts)
        for i, c in enumerate(horizontalCut):
            heapq.heappush(cuts, (-c, i, 'h'))

        for i, c in enumerate(verticalCut):
            heapq.heappush(cuts, (-c, i, 'v'))

        total = 0
        hmul, vmul = 1, 1
        while cuts:
            cost, idx, cut = heapq.heappop(cuts)
            cost *= -1
            if cut == 'h':
                total += hmul*cost
                vmul += 1
            else:
                total += vmul*cost
                hmul += 1

        return total
