class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(verticalCut.begin(), verticalCut.end());
        sort(horizontalCut.begin(), horizontalCut.end());

        int i = m - 2, j = n - 2;

        int h_taken = 0, v_taken = 0;
        long long ans = 0;

        while(1){
            if(i < 0 || j < 0) break;
            //cout<<"okay "<<i<<"  "<<j<<endl;
            if(horizontalCut[i] >= verticalCut[j]){
                h_taken++;
                ans += horizontalCut[i] * 1LL *(v_taken + 1);
                i--;
            }
            else{
                v_taken++;
                ans += verticalCut[j] * 1LL *(h_taken + 1);
                j--;
            }
        }
        while(i >= 0){
            h_taken++;
            ans += horizontalCut[i] * 1LL *(v_taken + 1);
            i--;
        }
        while(j >= 0){
            v_taken++;
            ans += verticalCut[j] * 1LL *(h_taken + 1);
            j--;
        }
        return ans;

    }
};
