class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort(key=lambda x: -x)
        verticalCut.sort(key=lambda x: -x)
        i, j = 0, 0
        hor_pieces = 1
        ver_pices = 1
        costs = 0
        while i<m-1 or j<n-1:
            if i==m-1:
                pick_hor = False
            elif j==n-1:
                pick_hor = True
            else:
                pick_hor = horizontalCut[i] > verticalCut[j]
            if pick_hor:
                costs += horizontalCut[i]*ver_pices
                hor_pieces += 1
                i += 1
            else:
                costs += verticalCut[j]*hor_pieces
                ver_pices += 1
                j += 1

        return costs
