def minCostToCutCake(m, n, horizontalCut, verticalCut):
    cuts = []
    for i in range(m - 1):
        cuts.append((horizontalCut[i], 'h'))
    for j in range(n - 1):
        cuts.append((verticalCut[j], 'v'))
    cuts.sort(reverse=True)

    horizontal_pieces = 1
    vertical_pieces = 1

    total_cost = 0

    for cost, cut_type in cuts:
        if cut_type == 'h':
            total_cost += cost * vertical_pieces
            horizontal_pieces += 1
        else:
            total_cost += cost * horizontal_pieces
            vertical_pieces += 1

    return total_cost

class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        return minCostToCutCake(m, n, horizontalCut, verticalCut)
