class Solution {

    /**
     * @param Integer $m
     * @param Integer $n
     * @param Integer[] $horizontalCut
     * @param Integer[] $verticalCut
     * @return Integer
     */
    function minimumCost($m, $n, $horizontalCut, $verticalCut) {
        $h = new \SplPriorityQueue();
        $v = new \SplPriorityQueue();
        $hr = 1;
        $vr = 1;
        $ans = 0;

        foreach ($horizontalCut as $hCut) {
            $h->insert($hCut, $hCut);
        }
        foreach ($verticalCut as $vCut) {
            $v->insert($vCut, $vCut);
        }

        $count = $m + $n - 2;
        for ($index = 0; $index < $count; ++ $index) {
            if ($h->isEmpty()) {
                ++ $hr;
                $ans += $v->extract() * $vr;
            } else if ($v->isEmpty()) {
                ++ $vr;
                $ans += $h->extract() * $hr;
            } else if ($h->top() > $v->top()) {
                ++ $vr;
                $ans += $h->extract() * $hr;
            } else {
                ++ $hr;
                $ans += $v->extract() * $vr;
            }
        }

        return $ans;
    }
}
