func minimumCost(m int, n int, horizontalCut []int, verticalCut []int) int64 {
	sort.Sort(sort.Reverse(sort.IntSlice(horizontalCut)))
	sort.Sort(sort.Reverse(sort.IntSlice(verticalCut)))

	horizontalCut = append(horizontalCut, 0)
	verticalCut = append(verticalCut, 0)

	var i1, i2 int
	var res int64

	for i1 < m && i2 < n {
		c1 := int64(horizontalCut[i1] * (i2 + 1))
		c2 := int64(verticalCut[i2] * (i1 + 1))

		if horizontalCut[i1] > verticalCut[i2] {
			res += c1
			i1++
		} else {
			res += c2
			i2++
		}
	}

	return res
}
