public class Solution {
    public long MinimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        var comparison = new Comparison<int>((i1, i2) => i2.CompareTo(i1));
        Array.Sort(horizontalCut, comparison);
        Array.Sort(verticalCut, comparison);

        long result = 0;
        int i = 0;
        int j = 0;

        int hoz = 1;
        int ver = 1;
        while(i < horizontalCut.Length && j < verticalCut.Length)
        {
            if (horizontalCut[i] > verticalCut[j])
            {
                result += horizontalCut[i] * ver;
                i++;
                hoz++;
            }
            else
            {
                result += verticalCut[j] * hoz;
                ver++;
                j++;
            }
        }

        while (i < horizontalCut.Length)
        {
            result += horizontalCut[i] * ver;
            i++;
        }

        while (j < verticalCut.Length)
        {
            result += verticalCut[j] * hoz;
            j++;
        }

        return result;
    }
}
