class Solution
{
public:
    long long minimumCost(int m, int n, vector<int> &hc, vector<int> &vc)
    {
        long long minCost = 0;
        long long hor= 1, ver=1,x = n+m-2;
        sort(hc.begin(), hc.end(), greater<int>());
        sort(vc.begin(), vc.end(), greater<int>());
        int i=0,j=0;
        while (i+j<x)
        {
            if(j < n-1 && i < m-1){
                if(hc[i] < vc[j]){
                    minCost += vc[j]*hor;
                    j++;
                    ver++;
                }
                else{
                    minCost += hc[i]*ver;
                    i++;
                    hor++;
                }
            }
            else if(i < m-1){
                minCost += hc[i]*ver;
                i++;
                hor++;
            }
            else if(j < n-1){
                minCost += vc[j]*hor;
                j++;
                ver++;
            }
        }
        return minCost;

    }
};
