class Solution {
public:
    long long minimumCost(int  m, int  n, vector<int  >&h, vector<int>& v) {
        sort(h.begin(),h.end());
        reverse(h.begin(),h.end());
      sort(v.begin(), v.end());
      reverse(v.begin(),v.end());

    long long  i= 0, j = 0;
    long long  seg1 = 1, seg2 = 1;
    long long  ans = 0LL;
    int N=h.size();
    int M=v.size();

    while (i<N && j < M) {
        if (h[i] >= v[j]) {
            ans +=(h[i] * seg2*1LL);
            i++;
            seg1++;
        } else {
            ans +=( v[j] * seg1*1LL);
            j++, seg2++;

        }
    }

  while (j < M) {
        ans += (v[j] * seg1)*1LL;
        j++,seg2++;

    }
    while (i<N) {
        ans +=(h[i] * seg2)*1LL;
        i++,seg1++;

    }



    return ans;
    }
};
