class Solution {
    public long minimumCost(int m, int n, int[] h, int[] v) {
        Arrays.sort(h);
        Arrays.sort(v);
        int cnth = 1;
        int cntv = 1;
        long ans = 0;
        int i = h.length - 1;
        int j = v.length - 1;
        while(i >= 0 && j >= 0) {
            if(h[i] > v[j]) {
                ans += h[i]*cntv;
                cnth++;
                i--;
            }
            else {
                ans += v[j]*cnth;
                cntv++;
                j--;
            }
        }

        while(i >= 0) {
            ans += h[i]*cntv;
            i--;
        }
        while(j >= 0) {
            ans += v[j]*cnth;
            j--;
        }
        return ans;
    }
}
