class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long, char>> v;
        for (long long cost : horizontalCut) {
            v.emplace_back(cost, 'H');
        }
        for (long long cost : verticalCut) {
            v.emplace_back(cost, 'V');
        }

        sort(v.begin(), v.end(),greater<pair<long long,long long>>());

        long long c1 = 1;
        long long c2 = 1;

        long long ans = 0;
        for (auto itr: v) {
            long long cost = itr.first;
            char type = itr.second;
            if (type == 'H') {
                ans += cost * c2;
                ++c1;
            } else {
                ans += cost * c1;
                ++c2;
            }
        }

        return ans;
    }
};
