class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:

        arr = []
        for i, n in enumerate(horizontalCut):
            arr.append([n, 'horizontal', i])

        for i, n in enumerate(verticalCut):
            arr.append([n, 'vertical', i])

        arr = sorted(arr, key=lambda x: x[0], reverse=True)
        total_cost = 0
        vertical = 1
        horizontal = 1
        for i in arr:
            if i[1] == 'horizontal':
                total_cost += horizontal * i[0]
                vertical += 1
            else:
                total_cost += vertical * i[0]
                horizontal += 1
        return total_cost
