class Solution {
public:
#define ll long long
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(begin(hc),end(hc),greater<int>());
        sort(begin(vc),end(vc),greater<int>());
        ll i=0,j=0,h=1,v=1;
        ll ans=0;
        while(i<hc.size() and j<vc.size()){
            if(hc[i]>vc[j]){
                ans+=(ll)(hc[i]*v);
                ++i;
                ++h;
            }else{
                ans+=(ll)(vc[j]*h);
                ++j;
                ++v;
            }
        }        
        while(i<hc.size()){
            ans+=(ll)(hc[i]*v);
            ++i;
            ++h;
        }
        while(j<vc.size()){
            ans+=(ll)(vc[j]*h);
            ++j;
            ++v;
        }
        return ans;
    }
};
