class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        vector<pair<long long,char>> pr;
        for(int i=0;i<hor.size();i++) pr.push_back({hor[i] , 'H'});
        for(int i=0;i<ver.size();i++) pr.push_back({ver[i] , 'V'});

        sort(pr.begin() , pr.end());
        long long ans = 0;
        long long h = 1 , v = 1;


        for(int i=pr.size()-1;i>=0;i--){
            long long cost = pr[i].first;
            char type = pr[i].second;

            if(type == 'H'){
                ans = ans + cost*v;
                h++;
            }
            else{
                ans = ans + cost*h;
                v++;
            }
        }
        return ans;   
}
};
