class Solution {
public:

    #define ll long long

    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {

        sort(hor.begin(),hor.end());
        sort(ver.begin(),ver.end());
        int a=m-2,b=n-2,h=0,v=0;
        ll ans=0;
        while(a>=0 || b>=0)
        {
            if(a>=0 && b>=0)
            {
                if(hor[a]>=ver[b])
                {
                    ans+=(hor[a]*(v+1));
                    h++;
                    a--;
                }
                else{
                    ans+=(ver[b]*(h+1));
                    v++;
                    b--;
                }

            }
            else if(a>=0)
            {
                ans+=(hor[a]*(v+1));
                    h++;
                    a--;
            }
            else{
                ans+=(ver[b]*(h+1));
                    v++;
                    b--;

            }
        }

        return ans;


    }
};
