class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        long long h = 1;
        long long v = 1;
        long long r = 0;
        sort(begin(hc),end(hc));
        sort(begin(vc),end(vc));
        int p1=m-2;
        int p2 = n-2;
        while(p1>=0 || p2>=0)
        {
            //cout<<p1<<" "<<p2<<" "<<r<<endl;
            if(p1<0 || (p2>=0 && vc[p2]>hc[p1]))
            {
                r+=h*vc[p2--];
                v++;
               // cout<<"vc"<<endl;
            }
            else
            {
                r+=v*hc[p1--];
                h++;
                //cout<<"hc"<<endl;
            }
        }
        return r;
    }
};
