class Solution:
    def minimumCost(self, m, n, ver, hor):
        ver = [(i, 'j') for i in ver]
        hor = [(i, 'i') for i in hor]

        a = sorted(ver + hor, reverse=True)
        v = h = 1
        res = 0
        for i, dirc in a:
            if dirc == 'j':
                res += h*i
                v += 1
            else:
                res += v*i
                h += 1

        return res
