class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hz, vector<int>& vc) {
        sort(hz.rbegin(), hz.rend());
        sort(vc.rbegin(), vc.rend());

        int h = 0, v = 0;
        int hp = 1, vp = 1;
        long long ans = 0;

        while (h < hz.size() && v < vc.size()) {
            if (hz[h] > vc[v]) {
                ans += hz[h] * vp;
                hp++;
                h++;
            } else {
                ans += vc[v] * hp;
                vp++;
                v++;
            }
        }

        while (h < hz.size()) {
            ans += hz[h] * vp;
            h++;
        }

        while (v < vc.size()) {
            ans += vc[v] * hp;
            v++;
        }

        return ans;
    }
};
