class Solution {
public:
    long long minimumCost(int rows, int cols, vector<int>& horzCuts, vector<int>& vertCuts) {
        sort(horzCuts.begin(), horzCuts.end());
        reverse(horzCuts.begin(), horzCuts.end());
        sort(vertCuts.begin(), vertCuts.end());
        reverse(vertCuts.begin(), vertCuts.end());
        int hIdx = 0, vIdx = 0, hPieces = 1, vPieces = 1;
        long long totalCost = 0;
        while (hIdx < rows-1 && vIdx < cols-1) {
            if (vertCuts[vIdx] <= horzCuts[hIdx]) {
                totalCost += horzCuts[hIdx] * 1ll*vPieces;
                hPieces++;
                hIdx++;
            } else {
                totalCost += vertCuts[vIdx] *1ll* hPieces;
                vIdx++;
                vPieces++;
            }
        }
        while (hIdx < rows-1) {
            totalCost += horzCuts[hIdx] *1ll* vPieces;
            hPieces++;
            hIdx++;
        }
        while (vIdx < cols-1) {
            totalCost += vertCuts[vIdx] *1ll*  hPieces;
            vPieces++;
            vIdx++;
        }
        return totalCost;

    }
};
