class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        from sortedcontainers import SortedList

        abasedSystems = SortedList(key = lambda x: x[1])

        for ea in range(len(horizontalCut)):
            abasedSystems.add((ea, horizontalCut[ea], 'H'))
        for ea in range(len(verticalCut)):
            abasedSystems.add((ea, verticalCut[ea], 'V'))

        h = 1
        v = 1
        ans = 0
        while len(abasedSystems):
            thee = abasedSystems.pop()

            if thee[2] == 'H':
                ans += thee[1] * h
                v += 1
            else:
                ans += thee[1] * v
                h += 1
        return ans
