class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hcnt, vcnt = 1,1
        hh, vh = [], []
        for c in horizontalCut:
            heappush(hh, -c)
        for c in verticalCut:
            heappush(vh, -c)

        res = 0
        while hh and vh:
            if -hh[0] > -vh[0]:
                cot = -1 * heappop(hh)
                res += cot * vcnt
                hcnt += 1
            else:
                cot = -1 * heappop(vh)
                res += cot * hcnt
                vcnt += 1
        left = hh if hh else vh
        leftcnt = vcnt if hh else hcnt
        for c in left:
            res += -c * leftcnt
        return res
