class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort(reverse=True)
        verticalCut.sort(reverse=True)

        i, j = 0, 0
        totalCost = 0
        h_cuts, v_cuts = 0, 0

        while i < m - 1 or j < n - 1:
            if i < m - 1 and (j >= n - 1 or horizontalCut[i] >= verticalCut[j]):
                totalCost += horizontalCut[i] * (v_cuts + 1)
                h_cuts += 1
                i += 1
            else:
                totalCost += verticalCut[j] * (h_cuts + 1)
                v_cuts += 1
                j += 1
        while i < m - 1:
            totalCost += horizontalCut[i] * (v_cuts + 1)
            i += 1

        while j < n - 1:
            totalCost += verticalCut[j] * (h_cuts + 1)
            j += 1
        return totalCost
