class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<long long,int>>total;
        for(auto&it:h)
            total.push_back({it,!(m<n)});
        for(auto&it:v)
            total.push_back({it,(m<n)});
        sort(total.rbegin(),total.rend());
        long long a=1,b=1;
        long long ans=0;
        for(auto&[x,y]:total)
        {
            if(m<n)
            {
                if(y)
                    ans+=x*a,b++;
                else
                    ans+=x*b,a++;
            }else{
                if(!y)
                    ans+=x*a,b++;
                else
                    ans+=x*b,a++;
            }
        }
        return ans;
    }
};
