class Solution {
public:



    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) 
    {
        int h = 0;
        int v = 0;
        int hp = 1;
        int vp = 1;
        long long c = 0;
        if (m > 1)
        {
            sort(hc.begin(), hc.begin() + m - 1);
            reverse(hc.begin(), hc.begin() + m - 1);
        }
        if (n > 1) 
        {
            sort(vc.begin(), vc.begin() + n - 1);
            reverse(vc.begin(), vc.begin() + n - 1);
        }
        // for(int x: hc) cout << x << ' ';
        while(h + 1 < m && v + 1 < n) 
        if (hc[h] > vc[v]) c += 1LL * hc[h] * vp, hp++, h++;
        else c += 1LL * vc[v] * hp, vp++, v++;
        while(v + 1 < n) c += 1LL * vc[v++] * hp;
        while(h + 1 < m) c += 1LL * hc[h++] * vp;
        return c;
    }
};
