class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        combined_cuts = [(cost, 'h') for cost in horizontalCut] + [(cost, 'v') for cost in verticalCut]
        combined_cuts.sort(reverse=True, key=lambda x: x[0])
        total_cost = 0
        horizontal_pieces = 1
        vertical_pieces = 1
        for cost, cut_type in combined_cuts:
            if cut_type == 'h':
                total_cost += cost * vertical_pieces
                horizontal_pieces += 1
            else:
                total_cost += cost * horizontal_pieces
                vertical_pieces += 1
        return total_cost
