class Solution(object):
    def minimumCost(self, m, n, horizontalCut, verticalCut):
        cutList = [(cost, 'H') for cost in horizontalCut] + [(cost, 'V') for cost in verticalCut]
        cutList.sort()
        cutList.reverse()
        totalCost = 0
        horizontalPieces = 1
        verticalPieces = 1

        for cost, cuttype in cutList:
            if cuttype == 'H':
                totalCost += cost * verticalPieces
                horizontalPieces += 1
            else:
                totalCost += cost * horizontalPieces
                verticalPieces += 1

        return totalCost
