class Solution {
    public long minimumCost(int m, int n, int[] hPrice, int[] vPrice) {
        long res = 0;
        Arrays.sort(hPrice);
        Arrays.sort(vPrice);
        int hCount = 1;
        int vCount = 1;
        int i = hPrice.length - 1;
        int j = vPrice.length - 1;
        while (i >= 0 && j >= 0) {
            if (hPrice[i] >= vPrice[j]) {
                hCount++;
                res += vCount * hPrice[i--];
            } else {
                vCount++;
                res += hCount * vPrice[j--];
            }
        }
        for (; i >= 0; i--) {
            res += vCount * hPrice[i];
        }
        for (; j >= 0; j--) {
            res += hCount * vPrice[j];
        }
        return res;
    }
}
