class Solution {
public:
  long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {

    priority_queue<pair<int, int>> pq;

    vector<int> cnt = {1, 1};

    for(auto &i: horizontalCut) pq.push({i, 0});
    for(auto &i: verticalCut) pq.push({i, 1});

    long long ans = 0;

    while(!pq.empty()) {

      auto it = pq.top();
      pq.pop();

      ans += cnt[it.second ^ 1] * it.first;
      cnt[it.second]++;

    }

    return ans;
  }
};
