class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        h = sorted(h)
        v = sorted(v)
        hh = list(accumulate(h))
        vv = list(accumulate(v))
        res = 0
        while h or v:
            if h and v:
                if h[-1] > v[-1]:
                    res += h.pop()
                    hh.pop()
                    res += vv[-1]
                else:
                    res += v.pop()
                    vv.pop()
                    res += hh[-1]
            elif h:
                res += hh.pop()
                h = []
            else:
                res += vv.pop()
                v = []
        return res
