class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int,int>>vc;
        for(int i=0;i<h.size();i++){
            vc.push_back({h[i],0});
        }
        for(int i=0;i<v.size();i++){
            vc.push_back({v[i],1});
        }
        sort(vc.rbegin(),vc.rend());

        long h1=1,v1=1;
        long long ans=0;
        for(int i=0;i<vc.size();i++){
            if(vc[i].second==0){
                ans+=vc[i].first*h1;
                v1++;
            }
            else{
                ans+=vc[i].first*v1;
                h1++;
            }
        }
        return ans;
    }
};
