class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut,
                          vector<int>& vCut) {
        sort(hCut.begin(), hCut.end());
        sort(vCut.begin(), vCut.end());

        long long hsize = hCut.size();
        long long vsize = vCut.size();

        long long i = hsize - 1, j = vsize - 1, h_parts = 1, v_parts = 1;
        long long ans = 0;

        while (i >= 0 && j >= 0) {
            if (vCut[j] > hCut[i]) {
                ans += vCut[j] * h_parts;
                v_parts++;
                j--;
            } else {
                ans += hCut[i] * v_parts;
                h_parts++;
                i--;
            }
        }

        while (i >= 0) {
            ans += hCut[i] * v_parts;
            i--;
        }

        while (j >= 0) {
            ans += vCut[j] * h_parts;
            j--;
        }

        return ans;
    }
};
