class Solution
{
public:
    long long minimumCost(int m, int n, vector<int> &ho, vector<int> &ve)
    {
        priority_queue<int> hq(ho.begin(), ho.end());
        priority_queue<int> vq(ve.begin(), ve.end());

        long long totalCost = 0;
        int hp = 1, vp = 1;

        while (!hq.empty() || !vq.empty())
        {
            if (hq.empty() || (!vq.empty() && vq.top() > hq.top()))
            {
                totalCost += vq.top() * hp;
                vp++;
                vq.pop();
            }
            else
            {
                totalCost += hq.top() * vp;
                hp++;
                hq.pop();
            }
        }

        return totalCost;
    }
};
