class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int, int>> a;
        for (int x : h) a.emplace_back(x, 0);
        for (int x : v) a.emplace_back(x, 1);
        sort(a.begin(), a.end());
        reverse(a.begin(), a.end());
        int cnt[2] = {0, 0};
        long long res = 0;
        for (auto [x, y] : a) {
            res += 1ll * x * (cnt[!y] + 1);
            ++cnt[y];
        }
        return res;
    }
};
