class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        sort(hCut.rbegin(), hCut.rend());
        sort(vCut.rbegin(), vCut.rend());
        long long hct = 1, vct = 1, i = 0, j = 0, ans = 0;
        while (i<hCut.size() && j<vCut.size()) {
            if (hCut[i]>=vCut[j]) {
                ans += hCut[i] * vct;
                hct++;
                i++;
            } else {
                ans += vCut[j] * hct;
                vct++;
                j++;
            }
        }
        while (i<hCut.size()) {
            ans+=hCut[i]*vct;
            i++;
        }
        while (j<vCut.size()) {
            ans+=vCut[j]*hct;
            j++;
        }
        return ans;
    }
};
