class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         vector<pair<int, char>> costs;
        for (int cost : horizontalCut)costs.push_back({cost, 'H'});
        for (int cost : verticalCut)costs.push_back({cost, 'V'});
        sort(costs.begin(), costs.end(), greater<>());

        long long int hp = 1, vp = 1, ans = 0;
        for (const auto& cut : costs) 
            if (cut.second == 'H') {
                ans += cut.first * vp;
                ++hp;
            } else {
                ans += cut.first * hp;
                ++vp;
            }

        return ans;
    }
};
