class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        int x = 1;
        int y = 1;
        long ans = 0;
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        while (x < n || y < m) {
            long yu = y <= m - 1 ? horizontalCut[m - 1 - y] : 0;
            long xu = x <= n - 1 ? verticalCut[n - 1 - x] : 0;
            // 那个大先切哪个
            if (xu >= yu) {
                ans += xu * y;
                x++;
            } else {
                ans += yu * x;
                y++;
            }
        }
        return ans;
    }
}
