class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        sort(hCut.begin(), hCut.end());
        sort(vCut.begin(), vCut.end());

        int i = hCut.size()-1;
        int j = vCut.size()-1;
        long long h=1, v=1;
        long long ans = 0;

        while(i>=0 && j>=0){
            if(hCut[i] > vCut[j]){
                ans += (hCut[i]*v);
                h++;
                i--;
            }
            else{
                ans += (vCut[j]*h);
                v++;
                j--;
            }
        }

        while(i>=0){
            ans += hCut[i]*v;
            i--;
        }

        while(j>=0){
            ans += vCut[j]*h;
            j--;
        }

        return ans;
    }
};
