class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        // greedy
        priority_queue<pair<int,pair<int,char>>> pq;
        for(int i=0;i<hcut.size();i++){
            pq.push({hcut[i],{i,'h'}});
        }
        for(int i=0;i<vcut.size();i++){
            pq.push({vcut[i],{i,'v'}});
        }
        int h=1,v=1;
        long long ans=0;
        while(!pq.empty()){
            auto t=pq.top();
            pq.pop();
            char direct=t.second.second;
            int ind=t.second.first;
            int cost=t.first;

            if(direct=='v'){
                h++;
                ans+=(cost*v);
            }
            else{
                v++;
                ans+=(cost*h);
            }
        }
        return ans;
    }
};
