class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCosts, vector<int>& vCosts) {
         priority_queue<pair<int, bool>> pq;

    for (int cost : hCosts) {
        pq.push({cost, true});
    }
    for (int cost : vCosts) {
        pq.push({cost, false});
    }

    int hPieces = 1, vPieces = 1;
    long long int totalCost = 0;

    while (!pq.empty()) {
        auto [cost, isH] = pq.top();
        pq.pop();

        if (isH) {
            totalCost += cost * vPieces;
            hPieces++;
        } else {
            totalCost += cost * hPieces;
            vPieces++;
        }
    }

    return totalCost;
    }
};
